      insert into MENTORING values(MENTORING_SEQ.nextval, '상위 1% 광고인이 알려주는 Action PPT 신공', '우리나라에서 가장 트렌디하고 설득력 높은 PPT를 만드는 곳은 어디일까요? 바로 슬라이드 한장, 발표 한 번에 목숨 거는 사람들이 모여있는 광고회사일 것입니다. 그 중에서도 국내 탑클래스의 광고회사, 고수들이 넘쳐나는 그곳에서 상위 1%의 PPT로 인정받는 사람의 문서는 얼마나 다를까요? 그리고 대체 어떤 스킬들이 쓰인 걸까요? 오랜 기간 실무에서 기획서, PT 발표문서를 주도적으로 작성하며  10년 동안 스스로 개발하고 익힌, 어디에서도 들을 수 없는 상위 1% 노하우를 여러분의 성공을 위해 아낌없이 전해드립니다! 저만 잘 따라오시면 학교는 물론 회사에서도 인정받는 PPT 고수로 거듭나시리라 약속합니다!', 30000, '경기', '서울 강북구 도봉로 10 ', 5, 'ppt.jpg', '#ppt#취업#능력자', SYSDATE , 4, 'g@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '유튜브의 이해부터 유튜브 개설까지', '유튜브를 시작하려고 많이들 생각하고 계십니다. 지금도 하루에 수십개의 채널이 개설되고, 많은 분들이 1-2달도 못 버티고 포기하시거나 채널을 방치하기가 일쑤입니다. 유튜브 알고리즘의 이해와 유튜브 시청층에 대한 이해부터 시작해서 어떤 컨텐츠를 만들어낼지 호스트와 함께 고민해보고 함께 개설하는 클래스를 만들어보았습니다. 대단한 장비도 필요없습니다. 아이폰7,8 이상급의 휴대폰, 갤럭시 노트8,9 이상급의 휴대폰이면 충분히 괜찮은 영상들을 만들어 낼 수 있습니다. 지금 여러분들은 충분히 준비가 되어있습니다. 시작하지 않을 이유는 아무것도 없습니다.', 30000, '서울', '서울 성북구 하월곡동', 3, '유튜브.jpg', '#유튜브#구독#좋아요', SYSDATE , 4,'g@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '내가 영화에 나온다고? 일반인 영화 제작', '누구나 쉽게 영화를 제작할 수 있습니다. 영화를 좋아하시는 분들을 위해 직접 배우, 연출, 제작 등 영화의 전반적인 부분들도 함께 해보면서 영화를 만들어 볼 수 있습니다. 함께 구성된 클래스 인원들과 영화 제작에 전반적인 내용을 배우면서 시나리오도 쓰고, 연출 방법에 대해서 고민을 나누며 영화를 실제 제작해볼 수 있습니다.', 30000, '서울', '청량리동 207-41', 3, '영화.jpg', '#영화#촬영#제작', SYSDATE , 4, 'g@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '느낌있는 인생 사진 남기기', '안녕하세요 반갑습니다 :) 윌로우 포토 입니다. 개인 스냅 촬영 / 커플 촬영 / 우정 사진 기념일을 맞이하는 연인들이나 좀 색다르게 데이트를 하고 싶으신 커플!! 친구들과 추억을 남기고 싶으신 분들!! 평생 남는 평생 간직할 수 있는 추억 남겨드려요. 야외 촬영 위주로 진행되며, 촬영 장소는 추후 선정합니다.', 30000, '서울', '서울 동대문구 홍릉로 111-2', 4, '인생사진.jpg', '#인생샷#사진#남는건사진', SYSDATE , 4, 'g@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '인스타그램 4시간 마스터 ', '인스타그램은 현재 전 세계 10억 명이 사용하고 있으며 국내 이용자 수는 천만 명 이상으로 빠르게 확신되고 있습니다. 초기 주 이용자층은 20~30대로 국한되었지만 이제는 10대부터 60대까지 다양한 연령대에서 이용하고 있습니다.본 과정은 인스타그램의 기초부터 특성을 이해하고 주요 기능을 마케팅적으로 어떻게 활용할 수 있는지와 콘텐츠 제작법을 4시간 동안 알려 드립니다.', 50000, '서울', '제기동 1212', 4, '인스타.jpg', '#인스타#마스터#초간단', SYSDATE , 4, 'g@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '생초보를 위한 파이널컷 프로', '기획부터 편집까지 전체를 꿰뚫고 있는 사람만이편집을 제대로 가르칠 수 있습니다.영상을 만들면서 가르쳐온지 이제 30년이 넘었습니다. 현장에서, 또 학교에서 학생들을 가르치면서 시간과 재능을 낭비하는 사람들을 많이 보아왔습니다. 이제 이 강의를 통해 쓸데없이 시간과 정력을 낭비하는 일을 이제 그만 멈추세요.', 20000, '경기', '안암동4가 23-3 ', 5, '파이널컷프로.jpg', '#파이널컷프로#편집#고수', SYSDATE , 4,'g@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '당 떨어진 당신을 위한 크림치즈 브라우니', '당 떨어진 당신을 위해 꾸덕꾸덕 크림치즈 브라우니 당 떨어진 당신을 위한 브라우니 클래스입니다. 꾸덕한 다크 초코 브라우니와 진한 크림치즈의 만남. 쌀쌀해진 요즘, 따뜻한 커피와 함께 브라우니 한입 해보세요. 하루의 피곤이 날아갈 거예요. 진하다 못해 꾸덕한 브라우니를 맛보면 다른 생각은 아예 안 들 거예요.', 30000, '서울', '상왕십리동 813', 2, '브라우니.jpg', '#브라우니#당#치즈', SYSDATE , 3, 'g@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '기념일엔! 마음가득 나만의 티라미수 만들기', '발렌타인데이...음...마음이 담긴 선물을 해야겠다! 이탈리아여행때 맛봤던 그 촉촉하고 부드러운 티라미수를 직접 만들어줘야지. 2007년부터 13년째 수제티라미수를 만들고 있는 라운지 엘 오소의 티라미수만들기 클래스로 오세요. 에스프레소머신부터 거품기까지 이탈리아에서 넘어온 레이디핑커비스켓부터 마스카포네 치즈까지 장비와 재료는 모두 준비되어 있습니다. 전해주고 싶은 따뜻한 마음만 가지고 오세요.', 50000, '서울', '서울 성동구 상왕십리동 336-7', 3, '티라미수.jpg', '#티라미수#기념일#커플', SYSDATE , 3, 'g@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '이제 집에서도 할 수 있다! 1:1 셀프 헤어 스타일링 레슨', '모든 사람들이 다람쥐 쳇바퀴 돌 듯 매일매일 반복되는 일상 속에서.. 가꾸지 못한 나를 거울로 바라볼 때에 가끔은 짜증이 나기도 하고 싫증이 나기도 하지만 그래도 내 생활 속에 전부가 되어버린 일상... 하지만  나 자신을 가꿀 수 있는 아름다움의 기술을 갖게 된다면 매일매일 거울을 볼 때에 아름다워지는 나를 보며  행복해질 수 있지 않을까요?  당신의 행복의 파트너가 되겠습니다..! 청담동 수석 실장 헤어디자이너 유호입니다.', 30000, '서울', '신당동 153-13', 3, '헤어.jpg', '#미용#헤어#스타일링', SYSDATE , 5, 'g@naver.com');
