      insert into MENTORING values(MENTORING_SEQ.nextval, '한의사와 함께하는 1:1 건강 코칭', '요즘 지속적으로 피로감을 느끼시고 계신가요? 아프니까 청춘이라지만 아픔을 묻어두면 청춘은 오래 가지 못합니다 :( 잠잘 시간도 부족할정도의 바쁜 일상과 계속 쌓여가는 피로와 스트레스, 나라에서도 보장해주지 않는 비싼 검진비에 오늘도 금방 좋아지겠지..라고 넘기고 계시진 않나요? 저절로 몸이 움츠러드는 차가운 병원이 아닌 카페보다 더 포근한 분위기 속에서 정성스레 우려낸 따뜻한 약차 한 잔을 마시며 우리 건강과 일상을 돌아보는 힐링 타임을 가져보아요 :)', 45000, '서울', '서울 강서구 화곡동', 4, '건강.jpg', '#건강#한의학#코칭', SYSDATE , 5, 'd@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '직접 배우고 체험하는 아로마 테라피 클래스', '당신의 빛나는 오늘을 생각합니다. 당신의 더 나은 내일을 응원합니다. 안녕하세요. 요가/명상/테라피를 사랑하는 Priya입니다 :) 아무리 좋은 것도 혼자 하는 것은 의미가 없음을 깨닫고, 함께하는 즐거움과 감사함을 만끽하고자 모임을 만들게 되었습니다. 주말 아침을 상쾌하고 활기차게 시작하실 분, 내면으로의 여행을 떠나고 싶으신 분, 채움과 비움의 균형을 맞추고 싶으신 분, 요가와 명상을 내 삶에 녹여내고 싶으신 분 모두 모두 환영합니다 :)', 40000, '경기', '서울 성북구 아리랑로 68 ', 4, '아로마테라피.jpg', '#아로마#테라피#치유', SYSDATE , 5, 'd@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '나의 라이프 스타일 이해하기 버크만 심리진단', '안녕하세요! 버크만 스토리센터입니다. 우리가 겪는 모든 일의 원인과 결과는 ‘나＇에서 비롯됩니다. 버크만 진단을 통해  나를 이해하고,  나와 내 주변 사람들과의 관계를 돌아보세요~  내가 원하는 것을 모르겠다… 대인관계가 어렵게 느껴진다… 버크만 스토리센터의  베이직 진단을 통해 자신의 성격 특성과  대인관계에서 있을 수 있는  스트레스를  관리해보세요! 나도 몰랐던 나 자신과  주변 사람들에  대해 알아보는 시간을 가져보세요.', 45000, '서울', '서울 서대문구 창천동', 5, '라이프_스타일.jpg', '#심리#라이프스타일#치유', SYSDATE , 5, 'd@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '왁싱으로 시원~한 여름을 준비하자 :)', '안녕하세요. 신촌역 현대백화점 5분거리 위치해 있는 더 예쁘지 입니다. 더예쁘지에서는 실력이 우수한 왁싱 전문 원장님이 고객님 개개인과의 1:1 상담 후,  맞춤 관리를 제공하고 있습니다. 원장님이 직접 엄선해 고른 이태리 천연 유기농 왁스를 사용해 피부에 오는 자극을 최소화합니다. 페이스, 바디, 브라질리언 부위마다  각각의 피부에 알맞는 전용 왁스를 사용하고 있습니다.', 35000, '서울', '서울 성북구 돈암동 42-32', 2, '왁싱.jpg', '#왁싱#제모#시원', SYSDATE , 5, 'd@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '바쁜 현대인에게 꼭! 필요한 하루 리프레쉬 타임, 사운드 명상!', '명상이 좋다는 이야기를 들어도 혼자 처음 시작하기 쉽지 않습니다. 또한, 명상의 경험이 있다고 하더라도 계속 이어나가기 참 쉽지 않습니다 명상할 때 가장 어려운 것이 명상 방석에 앉는 것이다. 라는 말이 있을 정도랍니다. 마음 챙김 명상 클래스는 소규모로 진행되는 작고 깊은 모임으로 일상적이고 구체적인 방법으로 명상하는 방법을 배우며, 스스로를 수용하고 치유하는 과정을 돕습니다. ', 50000, '서울', '서울 성북구 정릉로46길 17', 3, '명상.jpg', '#명상#힐링#리프레쉬', SYSDATE , 5, 'd@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '필라테스도 헬스도 지겹다면- 발레핏 배워보기', '발레는 배워보고 싶은데 너무 어려울 것 같고,  옷도 조금은 부담스러우신가요? 그렇다면 발레핏을 체험해보세요 :)발레의 효과는 그대로 가져가고, 발레보다는 쉽고, 부담스럽지 않은 발레핏! 발레를 운동처럼 최대한 쉽게 바꿔서 발레를 경험해 보실 수 있으실 거예요^^ 단체 레슨으로 개인 레슨만큼의 효과를 보시는 분들이 많고  재미있기 때문에 취미가 운동이 되는 것을 원하시는 분들에게 추천해요.', 30000, '서울', '길음동 21-71', 4, '발레핏.jpg', '#발레핏#교정#요가', SYSDATE , 5, 'd@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '오늘 할 필라테스를 내일로 미루지 말자', 'Urban Life ? 겉으로는 화려하지만 쉴 새 없이 돌아가는 일상 속에서 당신의 건강을 잃어가고 있지는 않으신가요? 과도한 업무 스트레스와 신경 쓰이는 인간관계 속에서 당신의 마음이 다치고 있지는 않으신가요? Urban Picnic !! 지친 몸과 마음을 힐링하는 필라테스 스튜디오로 도심 속 피크닉에 초대합니다. 강사의 경험과 전문성, 그리고 프라이버시가 보장되는 아늑한 스튜디오 공간에서 여러분들의 지친 몸과 마음을 치유하여 삶의 질을 높여드리겠습니다. :)', 30000, '경기', '서울 강북구 도봉로 10 ', 5, '필라테스.jpg', '#필라테스#맞춤케어#활력', SYSDATE , 5, 'g@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '영하 150도에서 지방 태우기, 크라이오테라피', '안녕하세요, 요가룩스는 요가(플라잉요가) & 기구필라테스 & 크라이오테라피로 몸과, 마음, 그리고 치유까지 고객님의 몸을 생각하며, 좋은 경험을 전하기 위해 노력하고있습니다. 유명한 연예인, 셀럽, 운동선수 등 이용을 하고있지요.다이어트, 리커버리, 통증감소, 불면증까지! 만병통치약이라해도 과언이 아닌 테라피입니다!', 30000, '서울', '서울 성북구 하월곡동', 3, '크라이오테라피.jpg', '#건강#크라이오테라피#다이어트', SYSDATE , 5,'g@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '뱃살 없는 건강한 식습관! 그린 스무디볼', '안녕하세요:) 오랜만에 그린 스무디볼 클래스를 오픈합니다! 날씨가 점점 추워지고, 옷이 두꺼워지고 있는데요. 나도 모르게 군살이 붙고 있진 않나요? 따뜻한 음식을 찾는 건 좋은데, 매번 짜고 자극적인 음식만 찾지는 않으신가요? 매일 아침, 일주일에 3번이라도 그린그린한 식습관을 만들어보는 건 어떨까요? 한 달 뒤 붓기가 조금은 사라진 여러분을 발견하실 수 있을 거에요!', 50000, '서울', '제기동 1212', 4, '그린_스무디볼.jpg', '#스무디#건강#로푸드', SYSDATE , 6, 'g@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '딸기 우유, 딸기 찹쌀떡 만들기', '성수에 위치한 포레스트데이 공방에서 진행되는 딸기원데이 클래스입니다. 딸기청과 생딸기 우유 각 500ml 한 병. 딸기 찹쌀떡 8구 (6구 세트포장, 2구 개별 포장)으로 총 3가지를 만들어 가져가는 수업입니다. 수업 시간은 대략 2시간 정도 내외로 소요됩니다. 처음 해보시는 분들도 손쉽게 따라 할 수 있어 어렵지 않아요~ 밑작업에 시간이 오래 걸리는 병소독을 제외하고 딸기 손질부터 떡찌기까지 모든 과정을 수업에서 직접 진행하실거에요~ 딸기향 가득한 공간에서 휴식같은 시간을 보내셨으면 좋겠습니다🍓', 20000, '경기', '안암동4가 23-3 ', 5, '딸기우유.jpg', '#딸기#우유#찹쌀떡', SYSDATE , 6,'g@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '건강과 맛까지 모두 담은 새콤달콤 수제청 만들기 ', '건강과 맛까지 모두 담은 새콤달콤 수제청 만들기 건강을 생각한다면 직접 만들어야죠 친절하고 다정한 리아언니와 함께하는 수제청 원데이 클래스입니다. 유쾌한 분위기에서 소수 인원으로 진행돼요. 리아쓰 부엌에서 만드는 수제청은 신선한 과일과 설탕 혹은 꿀 그리고 천연 과즙 외에 어떤 식품 첨가물도 넣지 않은 프리미엄 핸드메이드 제품입니다. 과일의 특성에 맞는 최고의 레시피로 건강하고 신선한 수제청을 만듭니다. 일반 단일청이 아닌 일정한 비율로 배합해서 만드는 하이퀄리티 혼합청을 만듭니다. 리아쓰 부엌만의 시크릿 레시피가 궁금하다면 신청하세요!', 30000, '서울', '상왕십리동 813', 2, '수제청.jpg', '#수제청#새콤달콤#건강', SYSDATE , 6, 'g@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '위스키 테이스팅부터 나만의 위스키 만들기까지 ', '안녕하세요 신사동에 위치한 스픽이지바 TASS입니다. 타스는 조용하면서도 아늑한 바입니다. 몰트 바에서 좋아하는 위스키를 자신 있게 주문하고 싶으신 분 본인도 모르게 스카치 위스키 (스코틀랜드에서 제조되는 위스키의 총칭)의전문가가 되실 수 있습니다. 테이스팅부터 나만의 블렌디드 위스키 만들기까지 함께 해 보실까요?', 50000, '서울', '서울 성동구 상왕십리동 336-7', 3, '위스키.jpg', '#위스키#테이스팅#설레임', SYSDATE , 6, 'g@naver.com');
      insert into MENTORING values(MENTORING_SEQ.nextval, '핸드드립(브루잉) 간지나게 내리기!', '요즈음 커피는 일상 속에서 빼놓을 수 없는 즐거움 중에 하나죠. 집에서 직접 핸드드립으로 커피를 내려드시는 분들도 많아지고 있어요. 커피 전문점에서 먹는 것처럼 집에서도 맛있게 핸드드립으로 내려드시고 싶으신 분들 그리고 핸드드립을 처음 접하시는 분들을 대상으로 기본적인 커피 이론 및 추출 원리를 알려드려요. 다양한 드리퍼들과 포트를 사용하여 수업을 진행합니다. 커피 매니악 제작소에서는 직접 로스팅 한 콩을 사용하여 다양한 종류의 원두와 신선한 원두로 수업하실 수 있다는게 장점입니다. 어떤 원두가 좋고 맛있는 원두인지 잘 몰라서 망설이던 분들도수업을 통해 노하우를 배워가세요. 부모님, 친구와 오셔도 좋고 데이트 겸 연인과 방문하셔도 즐거울 거에요.', 30000, '서울', '신당동 153-13', 3, '핸드드립.jpg', '#드립#브루잉#바리스타', SYSDATE , 6, 'g@naver.com');
